            for(let i =right; i>=left; i--){
                save[bottom][i] = count;
                count++
            }
            bottom--
            dir++        
        }
        if(dir == 3){
            for(let i =bottom; i>=top; i--){
                save[i][left] = count;
                count++
            }
            left++
            dir++        
        }
        dir = 0;
    }

    return save;
};
