            //-------------TOP --->> BOTTOM-------------//

            while (i < k) {
                arr[i][j] = ++count;
                i++;
                flag = true;
            }
            j--;
            i--;
            k--;

            //-----------RIGHT --->> LEFT---------//
            while (j >= n - l) {
                arr[i][j] = ++count;
                j--;
                flag = true;
            }
            j++;
            l--;
            i--;

            //----------BOTTOM --->> TOP-------------//
            while (i >= n - k) {
                arr[i][j] = ++count;
                i--;
                flag = true;
            }
            i++;
            j++;
        }


        return arr;
    }
}
