class Solution {
public:
   vector<vector<int>> generateMatrix(int n)
{
    int rr[] = {0, 1, 0, -1};
    int cc[] = {1, 0, -1, 0};

    vector<vector<int>> visited(n, vector<int>(n, 0));
    vector<vector<int>> ans(n, vector<int>(n, 0));
    visited[0][0] = 1;
    ans[0][0] = 1;

    int index = 0;
    int count = 1;

    int row = 0;
    int col = 0;

    while (count < n * n)
    {
        int r = row + rr[index];
        int c = col + cc[index];

        if (r < n and c < n and r >= 0 and c >= 0 and !visited[r][c])
        {
            count++;
            ans[r][c] = count;
            visited[r][c] = 1;
            row = r;
            col = c;
        }
        else
        {
            index = (index + 1) % 4;
        }
    }
    return ans;
}
};
```

