        for (int current = 1; current <= n * n; ++current) {
            matrix[r][c] = current;
            if (r + dr >= n || r + dr < 0 || c + dc >= n || c + dc < 0 || matrix[r + dr][c + dc] != 0) {
                swap(dr, dc);
                dr = -dr;
            }
            r += dr;
            c += dc;
        }
        return matrix;
