class Solution {
public:
    bool isNextValid(int x, int y, int direction_x, int direction_y, int n, vector<vector<int>> mat) {
        if ((x + direction_x < 0) || (x + direction_x >= n))
            return false;
        if ((y + direction_y < 0) || (y + direction_y >= n))
            return false;
        if (mat[y + direction_y][x + direction_x] != 0)
            return false;
        return true;
    }

    vector<vector<int>> generateMatrix(int n) {
        vector<vector<int>> mat(n, vector<int>(n));
        int direction_x = 1, direction_y = 0;
        int curr_x = 0, curr_y = 0;
        int count = 1;

        while (count <= pow(n, 2)) {
            mat[curr_y][curr_x] = count;

            // If next cell is out of range or filled already - change direction:
            if (!isNextValid(curr_x, curr_y, direction_x, direction_y, n, mat)) {
                swap(direction_x, direction_y);
                direction_x *= -1;
            }
            curr_x += direction_x;
            curr_y += direction_y;
            count++;
        }
        return mat;
    }
};
