public class Solution {
    public boolean repeatedSubstringPattern(String str) {
        int len = str.length();
        if(len<2) return false;
        for(int i=2;i<=len;i++){
            if(len%i!=0) continue;
            if(check(str, i)) return true;
        }
        return false;
