    public boolean check(String str, int repeat){
        int len = str.length();
        String cand = str.substring(0, len/repeat);
        for(int i=0;i<len;i++){
            if(str.charAt(i)!=cand.charAt(i%(len/repeat))) return false;
        }
        return true;
    }
}
