                j++;
                dp[i]=j;
                i++;
            }
            else
            {
                if(j!=0)
                {
                    j=dp[j-1];
                }
                else
                {
                    dp[i]=0;
                    i++;
                }
            }
        }

        return dp[n-1] && (dp[n-1]%(n-dp[n-1])==0);
    }
};

