class Solution {
public:
    bool repeatedSubstringPattern(string s) {
        deque<int>dq;
        for( int i = 0 ; i < s.size() ;i++){
            if( !dq.empty() && s[i] == dq.front()){
                dq.pop_front();
            }
            dq.push_back(s[i]);
        }

        if(dq.size() > s.size()/2) return false;
        int prevSize = dq.size();
        for( int i = 0 ; i < s.size()-dq.size() ;i++){
            if( !dq.empty() && s[i] == dq.front()){
                dq.pop_front();
            }
            dq.push_back(s[i]);
        }
        if( prevSize != dq.size()) return false;
        return true;
    }
};


