```csharp
public int MaxDepth(Node root) 
{	
	if(root == null)
	{
		return 0;
	}

	Queue<Node> queue = new Queue<Node>();
	queue.Enqueue(root);
	int depth = 0;

	while(queue.Count > 0)
	{
		int count = queue.Count;
		for(int i = 0; i < count; i++)
		{
			Node current = queue.Dequeue();
			foreach(var children in current.children)
			{
				queue.Enqueue(children);
			}
		}

		depth++;
	}
	return depth;
}
