class Solution {
 public:
  int MaxDepth(Node *root) {
    int max_depth = 0, depth;
    stack<pair<Node *, int>> s;

    if (root) s.push({root, 1});
    while (!s.empty()) {
      root = s.top().first;
      depth = s.top().second;
      s.pop();

      if (root->children.empty())
        max_depth = max(max_depth, depth);
      for (auto child : root->children)
        s.push({child, depth + 1});
    }
    return max_depth;
  }
};
