class Solution {
public:
    int arrayPairSum(vector<int>& nums) {
        int sum = 0, n = nums.size();
        vector<int> map(20001, 0);

        for(int i = 0; i < n; i++) map[nums[i]+10000]++;

        for(int i = 0, flag = 0; i < 20001;){
            if(map[i] > 0 && flag == 0){
                sum += i - 10000;
                flag = 1;
                map[i]--;
