```C++ []
class Solution {
public:
    int arrayPairSum(vector<int>& nums) {
        int min = nums[0];
        int max = nums[0];
        for (int num : nums)
        {
            if (num > max)
                max = num;
            if (num < min)
                min = num;
        }
        int *nums_count = new int[max - min + 1]{};
        for (auto num : nums)
        {
            nums_count[num - min] += 1;
        }
        int min_pair_sum = 0;
        int skip = false;
        for (int i = 0; i < (max - min + 1); i++)
        {
            while (nums_count[i] > 0)
            {
                if (false == skip)
                {
                    min_pair_sum += (i + min);
                    skip = true;
                }
                else 
                {
                    skip = false;
                }
                nums_count[i] -= 1;
            }    
        }
        delete[] nums_count;
        return min_pair_sum;
    }
};
```
