class Solution {
    public int arrayPairSum(int[] nums) {
        Arrays.sort(nums);
        int i = 0;
        int j = nums.length - 2;
        int n = nums.length / 2;
        int result = 0;
        boolean odd = false;

        if(n % 2 == 1) {
            odd = true;
        }

        while(i < j) {
            result += nums[i];
            result += nums[j];
            i += 2;
            j -= 2;
        }

        if(odd) {
            result += nums[i];
        }
        return result;
    }
}
