class Solution {
public:
    vector<vector<int>> insert(vector<vector<int>>& intervals, vector<int>& newInterval) 
    {
        // just remember the merge intervals problem
        // try to change this problem into that

        // add newInterval to the intervals
        intervals.push_back(newInterval);

        // now sort this
        sort(intervals.begin(),intervals.end());

        // now use the same logic that we used in merge intervals
        vector<vector<int>> ans;
        int n = intervals.size();
        int i=0,j=1;                                // using 2 pointers
        ans.push_back(intervals[i]);                // very first interval

        while(j<n)
        {
            // if my new interval is not overalpping with previous one then just add this
            if(intervals[j][0]>ans[i][1])
            {
                i++;                                // move previous pointer
                ans.push_back(intervals[j]);
            }
            // if it is overlapping then update prev second by curr second
            else
            {
                ans[i][1] = max(ans[i][1],intervals[j][1]);
            }

            j++;
        }

        return ans;
    }
};
```

