class Solution
{
public:
    vector<vector<int>> insert(vector<vector<int>> &intervals, vector<int> &new_interval)
    {
        vector<vector<int>> res;
        int n = intervals.size();
        for (int i = 0; i < n; i++)
        {
            int low = max(intervals[i][0], new_interval[0]);
            int high = min(intervals[i][1], new_interval[1]);

            // overlapping condition
            if (low <= high)
            {
                int union_low = min(intervals[i][0], new_interval[0]);
                int union_high = max(intervals[i][1], new_interval[1]);

                new_interval = {union_low, union_high};
            }
            else
            {
                if (intervals[i][1] < new_interval[0])
                {
                    res.push_back(intervals[i]);
                }
                else if (new_interval[1] < intervals[i][0])
                {
                    res.push_back(new_interval);
                    new_interval = intervals[i];
                }
            }
        }
        res.push_back(new_interval);
        return res;
    }
};
