class Solution:
    def insert(self, intervals: List[List[int]], newInterval: List[int]) -> List[List[int]]:
        st=-1
        end=-1
        n=len(intervals)
        if n==0:
            return [newInterval]
        for i in range(n):
            if st==-1:
                if newInterval[0]<=intervals[i][1]:
                    st=i
            if st!=-1:
                if newInterval[1]>=intervals[i][0]:
                    end=i+1
        if st==-1:
            intervals.append(newInterval)
        elif end==-1:
            intervals.insert(st,newInterval)
        else:
            ni=[min(newInterval[0],intervals[st][0]),max(newInterval[1],intervals[end-1][1])]
            intervals[st:end]=[ni]

        return intervals


