	class Solution {
		public int[][] insert(int[][] intervals, int[] newInterval) {

			// base condition
			if(intervals.length == 0)
				return new int[][]{newInterval};

			// define start and end
			int start  = newInterval[0];
			int end  = newInterval[1];

			List<int[]> output = new ArrayList<>();

			// to insert any interval in sorted array we need to find the place where it needs to be added
			// to do that we will first add all intervals in output list which has end less than new interval
			int i = 0;
			while(i < intervals.length && intervals[i][1] < start){
				output.add(new int[]{intervals[i][0],intervals[i][1]});
				i++;
			}

			// once we found the place where new interval should be added using above while loop we will use same logic as merge interval
			// we will compare new interval and current interval. If merging needed we will merge and add it in the list. 
			while(i < intervals.length){
				if(intervals[i][0] <= end){
					start = Math.min(start, intervals[i][0]);
					end = Math.max(end, intervals[i][1]);
				}
				else{
					output.add(new int[]{start,end});
					start = intervals[i][0];
					end = intervals[i][1];
				}
				i++;
			}

			// add last remaining interval
			output.add(new int[]{start, end});

			// convert list to 2d array
			int[][] insertInterval = output.toArray(new int[output.size()][]);

			// return
			return insertInterval;
		}
	}

