        List<Integer> ip = new ArrayList<>(); // ip = input
        for(int i = 0; i < nums.length; i++){
            ip.add(nums[i]);
        }
        List<Integer> op = new ArrayList<>(); // op = output
        List<List<Integer>> res = new ArrayList<>(); 

        solve(ip, op, res);
        return res;
