  }
  return res;
};
```


**Backtracking**
```
let subsets = function (nums) {
  const res = [];
  const backtracking = (nums, res, idx, path) => {
    res.push([...path]);

    for (let i = idx; i < nums.length; i++) {
      path.push(nums[i]);
      backtracking(nums, res, i + 1, path);
      path.pop();
    }
  };

  backtracking(nums, res, 0, []);

  return res;
};
```


**Lexicographic Subsets**
```
var subsets = function(nums) {
    let res = []
    const powSize = Math.pow(2, nums.length)

    for (let i = 0; i < powSize; i++) {
        let set = []
        for (let j = 0; j < nums.length; j++) {
            if ((i & (1 << j)) > 0) {
                set.push(nums[j])
            }
        }
        res.push(set)
    }
    return res
}
```

