        n, result = len(nums), []
        def powerSet(nums, i, subSet): 
            if i==n:
                result.append(subSet) 
                return 
            powerSet(nums, i+1, subSet) 
            powerSet(nums, i+1, subSet + [nums[i]]) 
        powerSet(nums, 0, [])
        return result 
