class Solution {
    public List<List<Integer>> subsets(int[] nums) {
        int n=nums.length;

        int range=1<<n;
        List<List<Integer>> ans=new ArrayList<>();
        for(int i=0;i<range;i++){
            int x=i;
            ArrayList<Integer> a=new ArrayList<>();
            int j=0;   
            while(x>0 && j<n){
                if((x&1)==1){
                    a.add(nums[n-1-j]);
                }
                j++;
                x=x>>1;
            }
            ans.add(a);

        }

        return ans;
    }
}
