public int firstBadVersion(int n) {
    // The basic idea can be illustrated below:
	// n = 6, firstBadVersion = 5
	// 1, 2, 3, 4, 5, 6
	// |     |        |
	// l     m        r
	// isBadVersion(m) = false, move right

	// 1, 2, 3, 4, 5, 6
	//          |  |  |
	//          l  m  r
	// isBadVersion(m) = true, move left

	// 1, 2, 3, 4, 5, 6
	//          |  |
	//          l  r
	//          m
	// isBadVersion(m) = false, move right

	// 1, 2, 3, 4, 5, 6
	//             |
	//             r
	//             l
	//             m
	// return l

	// Q: why do we need l = m + 1?
	// A: because l = m will end up to an infinite loop, for example:
	// n = 6, firstBadVersion = 5
	// 1, 2, 3, 4, 5, 6
	// |     |        |
	// l     m        r
	// isBadVersion(m) = false, move right

	// 1, 2, 3, 4, 5, 6
	//       |  |     |
	//       l  m     r
	// isBadVersion(m) = false, move right

	// 1, 2, 3, 4, 5, 6
	//          |  |  |
	//          l  m  r
	// isBadVersion(m) = true, move left

	// 1, 2, 3, 4, 5, 6
	//          |  |
	//          l  r
	//          m
	// isBadVersion(m) = false, move right
	// at this point, if set l = m, then l will remain its current index and lead to an infinite loop.

	if (isBadVersion(1)) {
		return 1;
	}

	int l = 1;
	int r = n;

	while (l < r) {
		int m = l + (r - l) / 2;
		if (isBadVersion(m)) {
			// move left
			r = m;
		} else {
			// move right
			l = m + 1;
		}
	}

	return l;
}
