// The API isBadVersion is defined for you.
// bool isBadVersion(int version);

class Solution {
public:
    int firstBadVersion(int n) {
		// Considering low as 1
		// And high as n
        int low = 1;
        int high = n;
		// Continuing in the loop until we found a answer
        while(true){
            int mid = low + (high - low)/2;
			// Checking the base conditin i.e, whether the first and the last values are bad or not
			// if so they are bad then it is understandable that we reached all the way through starting and ending
			// points so obviously they would be bad
            if((mid==n or mid==1) and isBadVersion(mid)==true) return mid;
			// checking for the current value is false and the next one is true, so the bad ones start from mid+1
            else if((isBadVersion(mid) == false) and (isBadVersion(mid+1) == true)) return mid+1;
			// checking if the mid value is false so as far we haven't encountered any false value so we can start from 
			// that position
            else if(isBadVersion(mid) == false) low = mid+1;
			// If we encountered true then the true value starts from some way before so we need to go back
            else high = mid-1;
        }
    }
};

```
