impl Solution {
    pub fn first_bad_version(&self, n: i32) -> i32 {
        let mut low = 1i32;
        let mut high = n;
        let mut mid = low + (high - low)/2;

        while low <= high {
            if self.isBadVersion(mid) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
            mid = low + (high - low)/2;
        }
        high + 1
    }
}
