            if(a[i][j] > k) j --;
            else{
                count += j + 1;  // +1 because it's zero based indexing
                i++;
            }
        }
        return count;
    }
public:
    int kthSmallest(vector<vector<int>>& a, int k) {
        if(a.size() == 0) return -1;
        int result = -1;
        int low = a[0][0], high = a[a.size()-1][a.size()-1];
        while(low <= high){
            int mid = low + ((high - low) >> 1);
            if(countGreater(a, mid) >= k){
                result = mid;
                high = mid - 1;
            }
            else low = mid + 1;
        }
        return result;
    }
};
