class Solution {
    int[][] matrix;
    int n;
    public int kthSmallest(int[][] matrix, int k) {
        this.matrix=matrix;
        n = matrix.length;
        int low = matrix[0][0];
        int high = matrix[n-1][n-1];
        int ans=0;
        while(low<=high)
        {
            int mid = low+(high-low)/2;
            if(count(mid)>=k)
            {
                ans=mid;
                high =mid-1;
            }
            else
            {
                low=mid+1;
            }
        }
        return ans;
    }
    int count(int mid)
    {
        int r=0;
        int c=n-1;
        int count=0;
        while(r<n && c>=0)
        {
            if(matrix[r][c]<mid)
            {
                r++;
                count=count+1+c;
            }
            else
            {
                while(c>=0 && matrix[r][c]>mid)
                    c--;
                count=count+c+1;
                r++;
            }
        }
        return count;
    }

}
