class Solution {
    public int kthSmallest(int[][] matrix, int k) {
        if (matrix == null || matrix.length == 0 
           || matrix[0] == null || matrix[0].length == 0)
            return -1;

        int m = matrix.length, n = matrix[0].length;
        int left = matrix[0][0], right = matrix[m - 1][n - 1];

        while (left < right) {
            int mid = left + (right - left) / 2, count = getCountLessThanOrEqualToTarget(matrix, mid);
            if (count < k)
                left = mid + 1; //we have not met our quota k so expand our search space
            else
                right = mid;
        }

        return left;
    }

    private int getCountLessThanOrEqualToTarget(int[][] matrix, int target) {
        int m = matrix.length, n = matrix[0].length, count = 0, j = n - 1;
        for (int i = 0; i < m; i++) {
            while (j >= 0 && matrix[i][j] > target)
                j--;
            count += j + 1;
        }
        return count;
    }
}
