class Solution {
public:
    int kthSmallest(vector<vector<int>>& arr, int k) {

        int n = arr.size();
        int l = arr[0][0],r = arr[n-1][n-1],ans = 0;
        while(l <= r){
            int mid = (l+r)/2;
            int cnt = 0;
            for(int i = 0;i < n;i++){
                auto it = upper_bound(arr[i].begin(), arr[i].end(), mid); // counting elements less than equal to mid
                cnt += (it - arr[i].begin());
            }
            if(cnt >= k){ // if number of elements smaller than/ equal to are greater than equal to k, then mid can be our answer
                ans = mid;
                r = mid - 1;
            }
            else
                l = mid + 1;
        }
        return ans;
    }
}; 
