int kthSmallest(vector<vector<int>> &matrix, int k)
{
    //Matrix given to us is sorted so we can apply BINARY SEARCH
    //This implementation will be very much similar to applying
    //BINARY SEARCH in a 1-D Array


    int n = matrix.size();
    //Size of matrix(No of rows)
    int starting = matrix[0][0];
    //First element
    int middle = 0;
    int ending = matrix[n - 1][n - 1] + 1;
    //Added 1 because k can be last element also
    int tempColCount;
    //Temporary pointer pointing to column index in a row
    int tempSmallElemCount;
    //Elements less than the middle element in the matrix

    while (starting < ending)
    {
        middle = starting + (ending - starting) / 2;
        tempColCount = n - 1;
        tempSmallElemCount = 0;
        for (int rowIndex = 0; rowIndex < n; rowIndex++)
        {
        //We are checking for each row 
        //For each row, we count the elements that are smaller then middle
            while (tempColCount >= 0 && matrix[rowIndex][tempColCount] > middle)
                tempColCount--;
		//Reducing the column index if the element is greater than middle

            tempSmallElemCount += tempColCount + 1;
        //We are counting the elements less than middle
        }
        if (tempSmallElemCount < k)
        //If we have not reached the kth elem
            starting = middle + 1;
        else
            ending = middle;
        //If we have exceeded kth element
    }
    return starting;
}
