class Solution {
public:

	// count of number of elements smaller than current possible candidate for answer
    bool possible(vector<vector<int>>& grid, int k, int val){
        int n=grid.size();

        int i=0, j=n-1;
        int c=0;
        while(i<n && j>=0){
            if(grid[i][j]>val){
                j--;
            }else{
                c+=j+1;
                i++;
            }
        }
        return (c>=k);
    }
    int kthSmallest(vector<vector<int>>& grid, int k) {
        int n=grid.size();
        int l=grid[0][0], r=grid[n-1][n-1];  // minimum and maximum values possible in grid

        while(l<r){
            int m=l+(r-l)/2;
            if(possible(grid,k,m)){
                r=m;
            }else{
                l=m+1;
            }
        }
        return l;
    }
};
