public:
    int kthSmallest(vector<vector<int>>& matrix, int k) {
        int m=matrix.size();
        int n=matrix[0].size();
        int lo = matrix[0][0];
        int hi = matrix[m-1][n-1]+1;
        while(lo < hi)
