class Solution {
public:
    int kthSmallest(vector<vector<int>>& matrix, int k) {
        priority_queue<int> pq;
        int n = matrix.size();

        for(int i=0;i<k;i++)
        pq.push(matrix[i/n][i%n]);

        int r = k/n, c = k%n;

        for(int i=r;i<n;i++)
