class Solution {
public:
    typedef pair<int, pair<int, int>> ppi;
    int kthSmallest(vector<vector<int>>& matrix, int k) {
        priority_queue<ppi, vector<ppi>, greater<ppi>> Q;
        int n = matrix.size();
        for (int i = 0; i < n; ++i) {
            Q.push({matrix[i][0], {i, 0}});
        }
        --k;
        while(k--) {
            auto node = Q.top();
            Q.pop();
            if (node.second.second + 1 < n) {
                Q.push({matrix[node.second.first][node.second.second + 1], {node.second.first, node.second.second + 1}});
            }
        }
        return Q.top().first;
    }
};
