We know that each element lies in [-1e9, 1e9] so we can Binary Search for the element in this range.
```
#define ll long long int
class Solution {
    ll n,ans;
public:
    bool check(vector<vector<int>>& a,ll k,ll m){
        int cnt=0;
        for(int i=0;i<n;i++){
            if(a[i][n-1]<=m)
                cnt+=n;
            else{
                int ind=lower_bound(a[i].begin(),a[i].end(),m+1)-a[i].begin();
                cnt+=ind;
            }
        }
        if(cnt>=k)
            ans=min(ans,m);
        return cnt>=k;
    }

    int kthSmallest(vector<vector<int>>& a, int k) {
        ll h=1e9+10;
        ll l=(-1*h);
        n=a.size();
        ans=h;
        while(l<=h){
            ll m=l+(h-l)/2;
            if(check(a,k,m))
                h=m-1;
            else
                l=m+1;
        }

        return ans;
    }
};
```

