public int kthSmallest(int[][] matrix, int k) {

    int n = matrix.length, lo = matrix[0][0], hi = matrix[n - 1][n - 1], ans = -1;

    while(lo <= hi){

        int mid = lo + (hi - lo) / 2;

        if(isPossible(matrix, mid, n, k)){
            ans = mid;
            hi = mid - 1;
        }else
            lo = mid + 1;
    }

    return ans;
}

public boolean isPossible(int[][] matrix, int d, int n, int k){

    int count = 0, i = n - 1, j = 0;

    while(i >= 0 && j < n){

        int val = matrix[i][j];
        if(val > d)
            i--;
        else{
            j++;
            count += i + 1;
        }
    }

    return count >= k;
}

