class Solution {
    public int kthSmallest(int[][] matrix, int k) {
        int length = matrix.length;
        int low = matrix[0][0];
        int high = matrix[length-1][length-1];

        while(low<high) {
            int mid = (low+high)/2;
            if(myCount(matrix, mid, length) < k) {
                low = mid + 1;
            } else {
                high = mid; // note here even if its equal to k we need one more iteration so that we can get the correct value
            }
        }
        return high; // here we can return high or low either
    }

    private int myCount(int[][] matrix, int valueToCompare, int length) {
        int row = 0;
        int col = length - 1;
        int count = 0;

        while(row<length && col>=0) {
            if(matrix[row][col] <= valueToCompare) {
                count += col + 1;
                row ++;
            } else {
                col --;
            }
        }
        return count;
    }
}
