    private class Point {
        int x;
        int y;
        int val;
        public Point(int x, int y, int val) {
            this.x = x;
            this.y = y;
            this.val = val;
        }
    }

    public int kthSmallest(int[][] matrix, int k) {
        int n = matrix.length;
        PriorityQueue<Point> minHeap = new PriorityQueue<Point>((
            (p1, p2) -> p1.val - p2.val
        ));
        boolean[][] visited = new boolean[n][n];
        minHeap.add(new Point(0, 0, matrix[0][0]));
        visited[0][0] = true;
        while (!minHeap.isEmpty() && k > 1) {
            Point curr = minHeap.poll();
            int x = curr.x, y = curr.y;
            if (y + 1 < n && !visited[x][y + 1]) {
                minHeap.add(new Point(x, y + 1, matrix[x][y + 1]));
                visited[x][y + 1] = true;
            }
            if (x + 1 < n && !visited[x + 1][y]) {
                minHeap.add(new Point(x + 1, y, matrix[x + 1][y]));
                visited[x + 1][y] = true;
            }
            k--;
        }
        return minHeap.poll().val;
    }
