        int n = A.length;
        int ans = 0;
        int j = n - 1;
        for (int i = 0; i < n; i++) {
            while (j >= 0 && A[i][j] > target) j--;
            if (j == -1) break;
            ans += (j + 1);
        }
        return ans;
