class Solution {

  public int kthSmallest(int[][] matrix, int k) {
      int m = matrix.length, n = matrix[0].length;
      int low = matrix[0][0], high = matrix[m - 1][n - 1];
        while (low <= high) {
            int mid = low+(high - low) / 2 ;
            int count = countNums(matrix,mid);
            if (count < k)
                low = mid + 1;
            else
                high = mid-1;
        }
        return low;
  }
  //Counts the numbers less than mid, using the sorted matrix search pattern.
    private int countNums(int[][] matrix,int mid){
        int count = 0;
        int i = 0;
        int j = matrix[0].length-1;   // initialize start to top right corner

        while(i<matrix.length&&j>=0){
            if(matrix[i][j]<=mid){
                count = count + j +1;    // adds j+1 elements, as column indexes are before current element
                i++;                                  // move to next row for bigger match
            } else{
                j--;                                  // move to previous column for smaller match
            }
        }
        return count;
    }
}
