// Checks for case-insensitive match between two strings
bool caseInsensitiveEqual(string &a, string &b){
	return equal(begin(a), end(a), begin(b), end(b), [](char &a, char &b){
	   return tolower(a) == tolower(b); 
	});
}
// Checks if given character is vowel
bool isVowel(char &c){
	c = tolower(c);
	return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
}
// Checks if both string can be made same after vowels replacement
bool vowelFreqCheck(string a, string b){
	if(size(a) != size(b)) return false;
	int n = size(a);
	for(int i = 0; i < n; i++)            
		if((!isVowel(a[i]) || !isVowel(b[i])) && tolower(a[i]) != tolower(b[i])) return false;
	return true;
}
vector<string> spellchecker(vector<string>& wordlist, vector<string>& queries) {
	int n = size(queries);
	vector<string> answer(n, "");
	for(int i = 0; i < n; i++){
		bool matched = false;
		// Check if exact match is found
		for(string& word : wordlist)
			if(word == queries[i]){ queries[i] = word; matched = true; break; }
		if(matched) continue; // move on to next query if match found
		// Check if case-insensitive match is found
		for(string& word : wordlist)
			if(caseInsensitiveEqual(word, queries[i])){ queries[i] = word; matched = true; break; }
		if(matched) continue; // move on to next query if match found
		// Check if after vowel-replacement, the strings match
		for(string& word : wordlist)
			if(vowelFreqCheck(word, queries[i])){ queries[i] = word; matched = true; break; }
		if(!matched) queries[i] = ""; // if no match found in any case return empty string
	}
	return queries;
}
```
