	return s;
}
bool isVowel(char &c){
	return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u';
}
// masking will help check if a word satisfies vowel-error condition
string maskVowel(string &s){
	for(char& c : s) if(isVowel(c)) c = '#';
	return s;
}
vector<string> spellchecker(vector<string>& wordlist, vector<string>& queries) {
	unordered_set original_words(begin(wordlist), end(wordlist));
	unordered_map<string, string> lowered, vowelMasked;
	string tmp;
	// we are reverse-iterating since we need to find the first matching word
	// so, we need to update maps to hold the first-most word satisfying a condition
	for(auto word = rbegin(wordlist); word != rend(wordlist); word++) 
		tmp = *word, lowered[toLowercase(tmp)] = *word, vowelMasked[maskVowel(tmp)] = *word;
	for(string& q : queries){
		// if exact word is found, continue since we already have it stored in queries[i] (Note : we are returing queries itself)
		if(original_words.count(q)) continue;
		// if query converted to lowercase matches with lower-cased word, return the corresponding mapped word from lowered hashmap
		else if(lowered.count(toLowercase(q))) q = lowered[q];
		// if query converted to lowercase & vowels masked matches with lower-cased vowel-masked word, return the mapped word from vowelMasked hashmap
		else if(vowelMasked.count(maskVowel(q))) q = vowelMasked[q];
		else q = ""; // if no match found, return empty string
	}
	return queries;
}
