public class Solution
{
    Random rand = new Random();
    double _r, _x, _y;

    public Solution(double radius, double x_center, double y_center)
    {
        _r = radius;
        _x = x_center;
        _y = y_center;
    }

    public double[] RandPoint()
    {
        var theta = 2.0 * Math.PI * rand.NextDouble();
        var r = _r * Math.Sqrt(rand.NextDouble()); // Math.Sqrt is important here for uniform density

        var x = Math.Cos(theta) * r + _x;
        var y = Math.Sin(theta) * r + _y;

        return new[] { x, y };
    }
}
