class Solution 
{
    double x{}, y{}, RR{};
public:
    Solution(double r, double x, double y) : RR{r*r}, x{x}, y{y}
    {   
    }

    vector<double> randPoint() 
    {
	    double phi = 2.*M_PI*((double) rand() / RAND_MAX),
			   r   = sqrt(RR*((double) rand() / RAND_MAX));
	    return {x+r*cos(phi), y+r*sin(phi)};
    }
};
