class Solution 
{
    double x{}, y{}, RR{};
public:
    Solution(double r, double x, double y) : RR{r*r}, x{x}, y{y}
    {   
    }

    vector<double> randPoint() 
    {
        auto p = complex(x,y)+polar(sqrt(RR*((double) rand() / RAND_MAX)), 2.*M_PI*((double) rand() / RAND_MAX));
        return {p.real(), p.imag()};
    }
};
