class Solution {
public:
    vector<string> wordSubsets(vector<string>& A, vector<string>& B) {
        vector<string> res;
        int totalB[26] = {0,};
        // find all unique characters in all words in B and also find max frequence of each char among all words
        // ex: B = ["abc", "abca", "accd"], unique chars and max freq will be "a:2, b:1, c:2, d:1"
        for (string b: B) {
            int alpha_b[26] = {0,};
            for (char c: b)
                alpha_b[c - 'a']++;
            for (int i = 0; i < 26; i++)
                totalB[i] = max(totalB[i], alpha_b[i]);
        }
        // Find if a word from A covers all unique characters and their frequencies of TotalB
        for (string a: A) {
            int totalA[26] = {0,};
            for (char c: a)
                totalA[c - 'a']++;
            bool flag = true;
            for (int i = 0; i < 26; i++) {
                if (totalB[i] > totalA[i]) {
                    flag = false;
                    break;
                }
            }
            if (flag)
                res.push_back(a);
        }
        return res;
    }
};
