class Solution {

    double lowX, highX;
    double radius, centerY, centerX;
    Random random = new Random();

    public Solution(double radius, double x_center, double y_center) {
        this.lowX = x_center - radius;
        this.highX = x_center + radius;
        this.radius = radius;
        this.centerY = y_center;
        this.centerX = x_center;
    }

    public double[] randPoint() {
        double randX = random(lowX, highX); // first get random X

        // We will apply pythagorus to find limits of random Y
        double baseX = centerX - randX; // base
        double hypotenuse = this.radius; // hypotenuse
        double yRad = Math.sqrt((hypotenuse * hypotenuse) - (baseX * baseX)); // height
        double randY = random(centerY - yRad, centerY + yRad); 
        return new double[]{randX, randY};
    }

    private double random(double low, double high) {
        return (low + (high - low) * random.nextDouble());
    }
}
