class Solution {
public:
    void dfs(int node, vector<vector<int>>& rooms, vector<bool>& visited) {
        if (visited[node])
            return;
        visited[node] = true;
        for (int r: rooms[node]) {
            if (visited[r] == false) {
                dfs(r, rooms, visited);
            }
        }
    }
    bool canVisitAllRooms(vector<vector<int>>& rooms) {
        int N = rooms.size();
        vector<bool> visited(N, false);
        dfs(0, rooms, visited);
        return all_of(visited.begin(), visited.end(), [](bool val) {
            return val == true;
        }) ? true : false;
    }
};
