public class Solution {
    public int WiggleMaxLength(int[] nums) {
        if (nums.Length < 2) 
            return nums.Length;

        int maxLen = 1;
        const int up = 1, down = -1;
        int directionY = 0;        
        for (int i = 1; i < nums.Length; i++) {
            var diff = nums[i] - nums[i-1];

            if (diff < 0 && directionY != down) 
            {         
                //peak
                directionY = -1;
                maxLen++;
            } 
            else if (diff > 0 && directionY != up) 
            {   
                //valley
                directionY = 1;
                maxLen++;
            }
        }
        return maxLen;
    }
}
