        while (r < n) {
            if (map[s.charAt(r)] != -1) {
                l = Math.max(map[s.charAt(r)] + 1, l);
            }
            map[s.charAt(r)] = r;
            ans = Math.max(ans, r - l + 1);
            r++;
        }
        return ans;
    }
}

